mapper.update:
update reservation_demo.classroom


        room_name = #{room_name},


        room_floor = #{room_floor},


        available_seat = #{available_seat},


        is_multimedia_room = #{is_multimedia_room}


where room_id = #{room_id}

update reservation_demo.room_available_time_info
set reservation_num = reservation_num - 1,
    available_num   = available_num + 1
where time_id = #{time_id}
  and room_id = #{room_id}
  and building_id = #{building_id}
  and available_date = #{available_date}

mapper.select:
select count(1) nums
from reservation_demo.student_reservation
where room_id = #{room_id}
