mapper.select:
select stu.s_name, sr.s_id sr_s_id, sr.room_id sr_room_id, sr.room_name, sr.time_id sr_time_id, sr.building_id sr_building_id, tb.time_begin, tb.time_end, sr.reservation_date, stu.s_phone_number, b.building_name ra_building_id, sr.state from reservation_demo.student stu, reservation_demo.time_table tb, reservation_demo.student_reservation sr, reservation_demo.room_available_time_info ra, reservation_demo.t_building b where stu.s_id = sr.s_id and sr.time_id = ra.time_id and sr.room_id = ra.room_id and sr.building_id = ra.building_id and sr.reservation_date = ra.available_date and ra.time_id = tb.time_id and ra.room_id = tb.room_id and ra.building_id = tb.building_id and sr.building_id = b.building_id
and (stu.s_id = #{s_id} or stu.s_name = #{s_id})
and stu.s_major = #{s_major}
and stu.s_year = #{s_year}

select sr.room_name, cr.room_floor, tb.building_name, tt.time_begin, tt.time_end, sr.reservation_date,cr.available_seat, count(sr.s_id) reservation_num from reservation_demo.t_building tb, reservation_demo.classroom cr, reservation_demo.time_table tt, reservation_demo.student_reservation sr, reservation_demo.room_available_time_info ra where sr.time_id = ra.time_id and sr.room_id = ra.room_id and sr.building_id = ra.building_id and sr.reservation_date = ra.available_date and ra.time_id = tt.time_id and ra.room_id = tt.room_id and ra.building_id = tt.building_id and tt.room_id = cr.room_id and tt.building_id = cr.building_id and cr.building_id = tb.building_id
and (cr.room_id = #{room_id} or cr.room_name = #{room_id})
and tt.time_begin = #{time_begin}
and tt.time_end = #{time_end}
and ra.available_date >= #{date_begin}
and ra.available_date <= #{date_end}
group by sr.room_name, cr.room_floor, tb.building_name, tt.time_begin, tt.time_end, sr.reservation_date,cr.available_seat

select cr.room_name,
cr.room_floor,
tb.building_location,
tb.building_name,
tt.time_begin,
tt.time_end,
ra.available_date,
ra.available_num,
ra.reservation_num,
ra.time_id ra_time_id,
ra.room_id ra_room_id,
ra.building_id ra_building_id
from reservation_demo.t_building tb,
reservation_demo.classroom cr,
reservation_demo.time_table tt,
reservation_demo.room_available_time_info ra
where cr.building_id = tb.building_id
and ra.time_id = tt.time_id
and ra.room_id = tt.room_id
and ra.building_id = tt.building_id
and tt.room_id = cr.room_id
and tt.building_id = cr.building_id

    and tb.building_location = #{selectLocation}


    and tb.building_name = #{selectBuildingName}


    and cr.room_name = #{selectRoomName}


    and cr.room_floor = #{selectRoomFloor}


    and tt.time_begin = #{time_begin}


    and tt.time_end = #{time_end}


    and ra.available_date >= #{date_begin}


    and ra.available_date <= #{date_end}


    and


            ra.room_id = #{room_id} and ra.time_id = #{time_id}
